#include<iostream>#include<cmath>#include<vector>#define pb push_backusing namespace std;int a, b, c, d, lcm;int main () {        cin >> a >> b >> c;        lcm = (((a * b) / __gcd(a, b)) * c) / __gcd(((a * b) / __gcd(a, b)), c);    a = lcm/a;    b = lcm/b;    c = lcm/c;    d = lcm - a - b - c;    vector<int> as,bs,us;        for(int i = 0; i < a; ++i)        as.pb(i);    for(int i = a; i < b+a; ++i)        bs.pb(i);    for(int i = b+a; i < c+b+a; ++i){        bs.pb(i);        as.pb(i);    }    for(int i = b+a+c; i < c+b+a+d; ++i)        us.pb(i);        cout << "Set A : ";    for(vector<int>::iterator i = as.begin(); i != as.end(); ++i)        cout << *i << " ";    cout << endl << "Set B : ";    for(vector<int>::iterator i = bs.begin(); i != bs.end(); ++i)        cout << *i << " ";    cout << endl << "Universal Set : ";    for(vector<int>::iterator i = as.begin(); i != as.end(); ++i)        cout << *i << " ";    for(vector<int>::iterator i = bs.begin(); i != bs.end(); ++i)        cout << *i << " ";    for(vector<int>::iterator i = us.begin(); i != us.end(); ++i)        cout << *i << " ";}